script:
# THE DRAGON OF MOX — A colossal dragon sculpture coiled around a volcanic spire
build.clear()

# === VOLCANIC MOUNTAIN BASE ===
for y in range(0, 35):
    r = int(28 * (1 - y / 45.0))
    for x in range(-32, 32):
        for z in range(-32, 32):
            dist = (x*x + z*z) ** 0.5
            if dist <= r:
                build.setblock(x, y, z, "deepslate")
            elif dist <= r + 1:
                build.setblock(x, y, z, "cobblestone")

# Lava core
for y in range(0, 30):
    r = max(1, int(5 * (1 - y / 30.0)))
    for x in range(-r, r+1):
        for z in range(-r, r+1):
            if x*x + z*z <= r*r:
                build.setblock(x, y, z, "glowstone")

# Lava pool at top
for x in range(-4, 5):
    for z in range(-4, 5):
        if x*x + z*z <= 16:
            build.setblock(x, 33, z, "orange_concrete")
            build.setblock(x, 34, z, "yellow_concrete")

# === THE DRAGON — coiled around the mountain ===
# Dragon body spiraling upward
body_radius = 3
for t in range(0, 720):
    angle = math.radians(t)
    progress = t / 720.0
    spiral_r = 18 - progress * 10
    y = int(progress * 50) + 2
    cx = int(spiral_r * math.cos(angle))
    cz = int(spiral_r * math.sin(angle))

    # Body cross-section
    br = max(1, int(body_radius * (1 - progress * 0.4)))
    for dx in range(-br, br+1):
        for dy in range(-br, br+1):
            if dx*dx + dy*dy <= br*br:
                # Determine body color
                if abs(dx) <= br//2 and dy < 0:
                    block = "gold_block"  # Belly
                else:
                    block = "red_concrete"  # Scales
                bx = cx + dx
                bz = cz
                by = y + dy
                build.setblock(bx, by, cz, block)
                # Add depth
                if br > 1:
                    build.setblock(cx, by, cz + dx, block)

# === DRAGON HEAD at the top ===
head_y = 52
head_x, head_z = 5, 0

# Head shape - large and menacing
for dx in range(-4, 5):
    for dy in range(-3, 4):
        for dz in range(-6, 3):
            dist = (dx*dx/16 + dy*dy/9 + dz*dz/36)
            if dist <= 1:
                build.setblock(head_x+dx, head_y+dy, head_z+dz, "red_concrete")

# Snout extension
for dz in range(-10, -5):
    for dx in range(-3, 4):
        for dy in range(-2, 2):
            if abs(dx) + abs(dy) <= 3:
                build.setblock(head_x+dx, head_y+dy, head_z+dz, "red_concrete")

# Eyes - glowing
build.setblock(head_x-3, head_y+1, head_z-4, "glowstone")
build.setblock(head_x+3, head_y+1, head_z-4, "glowstone")
build.setblock(head_x-3, head_y+1, head_z-3, "yellow_concrete")
build.setblock(head_x+3, head_y+1, head_z-3, "yellow_concrete")

# Mouth - open with fire
for dx in range(-2, 3):
    build.setblock(head_x+dx, head_y-2, head_z-9, "orange_concrete")
    build.setblock(head_x+dx, head_y-1, head_z-9, "yellow_concrete")
    build.setblock(head_x+dx, head_y-2, head_z-10, "yellow_concrete")

# Horns
for i in range(0, 6):
    build.setblock(head_x-3, head_y+3+i, head_z+i-1, "black_concrete")
    build.setblock(head_x+3, head_y+3+i, head_z+i-1, "black_concrete")

# === WINGS — massive, spread wide ===
for wing_side in [-1, 1]:
    # Wing bones
    for i in range(0, 25):
        wx = head_x + wing_side * (i + 3)
        wy = head_y - 5 + int(3 * math.sin(i * 0.15))
        build.setblock(wx, wy, head_z, "black_concrete")
        build.setblock(wx, wy+1, head_z, "black_concrete")

        # Wing membrane
        for j in range(0, max(1, 15 - i//2)):
            membrane_z = head_z + j + 1
            membrane_y = wy - j // 3
            if -32 <= wx <= 31 and -32 <= membrane_z <= 31:
                build.setblock(wx, membrane_y, membrane_z, "red_wool")

    # Wing finger bones
    for finger in range(3):
        for i in range(0, 12 - finger*3):
            fx = head_x + wing_side * (15 + i + finger*3)
            fy = head_y - 5 - finger*2 + int(2*math.sin(i*0.2))
            fz = head_z + finger * 4
            if -32 <= fx <= 31:
                build.setblock(fx, fy, fz, "black_concrete")

# === TAIL — whipping around the base ===
for t in range(0, 200):
    progress = t / 200.0
    angle = math.radians(t * 2 + 300)
    tr = 22 + progress * 8
    tx = int(tr * math.cos(angle))
    tz = int(tr * math.sin(angle))
    ty = 3 + int(progress * 5)
    tail_r = max(1, int(3 * (1 - progress)))

    for dx in range(-tail_r, tail_r+1):
        for dy in range(-tail_r, tail_r+1):
            if dx*dx + dy*dy <= tail_r*tail_r and -32<=tx+dx<=31 and -32<=tz<=31:
                build.setblock(tx+dx, ty+dy, tz, "red_concrete")

# Tail spikes at end
for i in range(0, 4):
    sx = int(30 * math.cos(math.radians(500 + i*20)))
    sz = int(30 * math.sin(math.radians(500 + i*20)))
    for dy in range(0, 5-i):
        if -32<=sx<=31 and -32<=sz<=31:
            build.setblock(sx, 8+dy+i, sz, "black_concrete")

# === TREASURE HOARD at base ===
for x in range(-15, 16):
    for z in range(10, 25):
        if random.random() < 0.3:
            build.setblock(x, 1, z, "gold_block")
        elif random.random() < 0.3:
            build.setblock(x, 1, z, "emerald_block")

# Scattered gems
for _ in range(50):
    gx = random.randint(-20, 20)
    gz = random.randint(-20, 20)
    build.setblock(gx, 1, gz, random.choice(["diamond_block", "emerald_block", "gold_block"]))

# THE CELESTIAL PALACE — A floating sky palace with islands and bridges
build.clear()

# === MAIN FLOATING ISLAND ===
# Bottom of island - rough stone underside
for y in range(10, 18):
    r = int(20 * (1 - abs(y - 14) / 6.0))
    for x in range(-r, r+1):
        for z in range(-r, r+1):
            if x*x + z*z <= r*r:
                if y < 14:
                    build.setblock(x, y, z, "stone")
                elif y < 17:
                    build.setblock(x, y, z, "dirt")
                else:
                    build.setblock(x, y, z, "grass_block")

# Waterfalls cascading down from island
for wx, wz in [(12, 0), (-12, 0), (0, 12), (0, -12)]:
    for y in range(0, 14):
        build.setblock(wx, y, wz, "blue_stained_glass")
        build.setblock(wx+1, y, wz, "blue_stained_glass")
    # Pool at bottom
    for dx in range(-2, 3):
        for dz in range(-2, 3):
            if dx*dx + dz*dz <= 6:
                build.setblock(wx+dx, 0, wz+dz, "water")

# === THE PALACE on the main island ===
palace_y = 18

# Palace floor
build.fill(-14, palace_y, -14, 13, palace_y, 13, "quartz_block")

# Central grand hall
hall_h = 20
for y in range(palace_y+1, palace_y+hall_h):
    # Outer walls - ornate
    build.fill(-12, y, -12, 11, y, -12, "quartz_block")
    build.fill(-12, y, 11, 11, y, 11, "quartz_block")
    build.fill(-12, y, -12, -12, y, 11, "quartz_block")
    build.fill(11, y, -12, 11, y, 11, "quartz_block")

# Windows - large arched
for i in range(-8, 9, 4):
    for y in range(palace_y+3, palace_y+hall_h-3):
        build.setblock(-12, y, i, "white_stained_glass")
        build.setblock(11, y, i, "white_stained_glass")
        build.setblock(i, y, -12, "white_stained_glass")
        build.setblock(i, y, 11, "white_stained_glass")

# Gold pillars at corners
for px, pz in [(-11,-11),(10,-11),(-11,10),(10,10)]:
    for y in range(palace_y+1, palace_y+hall_h):
        build.setblock(px, y, pz, "gold_block")

# Interior pillars
for px in range(-8, 9, 4):
    for pz in range(-8, 9, 4):
        if abs(px) > 2 or abs(pz) > 2:
            for y in range(palace_y+1, palace_y+hall_h):
                build.setblock(px, y, pz, "quartz_block")

# Dome roof
for y in range(0, 15):
    r = int(12 * math.cos(y * math.pi / 30))
    if r < 1:
        r = 1
    for x in range(-r, r+1):
        for z in range(-r, r+1):
            if abs(x*x + z*z - r*r) <= r:
                build.setblock(x, palace_y+hall_h+y, z, "quartz_block")

# Dome tip
for y in range(palace_y+hall_h+12, palace_y+hall_h+20):
    build.setblock(0, y, 0, "gold_block")
build.setblock(0, palace_y+hall_h+20, 0, "sea_lantern")

# Floor design - sun pattern
for angle in range(0, 360, 15):
    rad = math.radians(angle)
    for dist in range(1, 10):
        x = int(dist * math.cos(rad))
        z = int(dist * math.sin(rad))
        build.setblock(x, palace_y, z, "gold_block")

# Throne at center
build.fill(-1, palace_y+1, -1, 1, palace_y+1, 1, "quartz_block")
build.fill(0, palace_y+2, 0, 0, palace_y+4, 0, "diamond_block")
build.setblock(0, palace_y+5, 0, "sea_lantern")

# === FOUR FLOATING SATELLITE ISLANDS ===
satellite_positions = [(-24, -24), (-24, 23), (23, -24), (23, 23)]
for sx, sz in satellite_positions:
    # Small floating island
    for y in range(15, 22):
        r = max(1, int(8 * (1 - abs(y - 18) / 5.0)))
        for dx in range(-r, r+1):
            for dz in range(-r, r+1):
                if dx*dx + dz*dz <= r*r:
                    ix = sx + dx
                    iz = sz + dz
                    if -32 <= ix <= 31 and -32 <= iz <= 31:
                        if y < 18:
                            build.setblock(ix, y, iz, "stone")
                        elif y < 21:
                            build.setblock(ix, y, iz, "dirt")
                        else:
                            build.setblock(ix, y, iz, "grass_block")

    # Tower on each island
    for y in range(22, 42):
        for dx in range(-2, 3):
            for dz in range(-2, 3):
                ix = sx + dx
                iz = sz + dz
                if -32 <= ix <= 31 and -32 <= iz <= 31:
                    if abs(dx) == 2 or abs(dz) == 2:
                        build.setblock(ix, y, iz, "quartz_block")
                    elif dx == 0 and dz == 0:
                        pass  # hollow
                    elif (y % 4 == 0) and (abs(dx)==1 or abs(dz)==1):
                        build.setblock(ix, y, iz, "white_stained_glass")

    # Spire on each tower
    for y in range(42, 52):
        ix = min(31, max(-32, sx))
        iz = min(31, max(-32, sz))
        build.setblock(ix, y, iz, "quartz_block")
    build.setblock(min(31,max(-32,sx)), 52, min(31,max(-32,sz)), "sea_lantern")

# === BRIDGES connecting islands to center ===
for sx, sz in satellite_positions:
    steps = 40
    for t in range(steps):
        progress = t / float(steps)
        bx = int(sx * (1 - progress))
        bz = int(sz * (1 - progress))
        by = 20 + int(5 * math.sin(progress * math.pi))

        # Bridge width
        if sx < 0:
            for dw in range(-1, 2):
                build.setblock(bx, by, bz+dw, "quartz_block")
                build.setblock(bx, by+1, bz+dw-1 if dw==-1 else bz+dw+1 if dw==1 else bz+dw, "air")
        else:
            for dw in range(-1, 2):
                build.setblock(bx+dw, by, bz, "quartz_block")

        # Railings
        if t % 3 == 0:
            build.setblock(bx-1, by+1, bz, "iron_block")
            build.setblock(bx+1, by+1, bz, "iron_block")
            build.setblock(bx, by+1, bz-1, "iron_block")
            build.setblock(bx, by+1, bz+1, "iron_block")

# === HANGING GARDENS under main island ===
for _ in range(30):
    vx = random.randint(-15, 14)
    vz = random.randint(-15, 14)
    vine_len = random.randint(3, 9)
    for vy in range(vine_len):
        build.setblock(vx, 10-vy, vz, "oak_leaves")

# === CLOUD LAYER decoration ===
for _ in range(20):
    cx = random.randint(-28, 27)
    cz = random.randint(-28, 27)
    cy = random.randint(5, 10)
    cloud_r = random.randint(2, 5)
    for dx in range(-cloud_r, cloud_r+1):
        for dz in range(-cloud_r, cloud_r+1):
            if dx*dx + dz*dz <= cloud_r*cloud_r and -32<=cx+dx<=31 and -32<=cz+dz<=31:
                build.setblock(cx+dx, cy, cz+dz, "white_wool")

# === LANTERNS floating in air ===
for _ in range(60):
    lx = random.randint(-25, 24)
    lz = random.randint(-25, 24)
    ly = random.randint(22, 45)
    build.setblock(lx, ly, lz, "lantern")

# THE ETERNAL COLOSSEUM v2 — Massively upgraded
build.clear()

# === GRAND FOUNDATION — Raised platform ===
for x in range(-32, 32):
    for z in range(-32, 32):
        dist = (x*x + z*z) ** 0.5
        if dist <= 31:
            build.setblock(x, 0, z, "stone_bricks")
            if dist <= 30:
                build.setblock(x, 1, z, "stone_bricks")

# === ARENA FLOOR — Ornate pattern ===
for x in range(-20, 21):
    for z in range(-20, 21):
        dist = (x*x + z*z) ** 0.5
        if dist <= 20:
            # Concentric ring pattern
            if int(dist) % 4 == 0:
                build.setblock(x, 2, z, "red_sand")
            elif int(dist) % 4 == 2:
                build.setblock(x, 2, z, "gold_block")
            else:
                build.setblock(x, 2, z, "sand")

# Central mosaic - compass rose
for angle in range(0, 360, 15):
    rad = math.radians(angle)
    for d in range(1, 8):
        x = int(d * math.cos(rad))
        z = int(d * math.sin(rad))
        if angle % 90 == 0:
            build.setblock(x, 2, z, "diamond_block")
        elif angle % 45 == 0:
            build.setblock(x, 2, z, "emerald_block")
        else:
            build.setblock(x, 2, z, "gold_block")

# === UNDERGROUND HYPOGEUM — Detailed chambers ===
for y in range(-10, 0):
    for x in range(-19, 20):
        for z in range(-19, 20):
            if x*x + z*z <= 361:
                build.setblock(x, y, z, "air")
    # Circular walls
    for angle in range(360):
        rad = math.radians(angle)
        x = int(19 * math.cos(rad))
        z = int(19 * math.sin(rad))
        build.setblock(x, y, z, "stone_bricks")
    # Pillars in grid
    for px in range(-15, 16, 5):
        for pz in range(-15, 16, 5):
            if px*px + pz*pz < 320:
                build.setblock(px, y, pz, "stone_bricks")
                # Decorative capitals at top
                if y == -1:
                    for dx in [-1, 0, 1]:
                        for dz in [-1, 0, 1]:
                            build.setblock(px+dx, y, pz+dz, "stone_bricks")

# Underground corridors (cross-shaped)
for y in range(-8, 0):
    for i in range(-18, 19):
        for w in range(-1, 2):
            build.setblock(i, y, w, "air")  # N-S corridor
            build.setblock(w, y, i, "air")  # E-W corridor
    # Corridor floors
    for i in range(-18, 19):
        build.setblock(i, -10, -1, "stone_brick_stairs")
        build.setblock(i, -10, 0, "stone_bricks")
        build.setblock(i, -10, 1, "stone_brick_stairs")
        build.setblock(-1, -10, i, "stone_brick_stairs")
        build.setblock(0, -10, i, "stone_bricks")
        build.setblock(1, -10, i, "stone_brick_stairs")

# Underground lighting
for i in range(-15, 16, 5):
    build.setblock(i, -1, 0, "lantern")
    build.setblock(0, -1, i, "lantern")
    build.setblock(i, -5, 0, "lantern")
    build.setblock(0, -5, i, "lantern")

# Iron grates in arena floor
for gx in range(-12, 13, 4):
    for gz in range(-12, 13, 4):
        if gx*gx + gz*gz < 180 and (gx != 0 or gz != 0):
            build.setblock(gx, 2, gz, "iron_block")
            build.setblock(gx, 1, gz, "iron_block")

# === OUTER WALLS — 4 tiers with proper arches ===
levels = 4
level_height = 10
outer_r = 30
inner_r = 22

for level in range(levels):
    base_y = 3 + level * level_height
    cur_outer = outer_r - level * 1
    cur_inner = inner_r

    for y in range(base_y, base_y + level_height):
        for angle in range(0, 360):
            rad = math.radians(angle)

            # Outer wall
            ox = int(cur_outer * math.cos(rad))
            oz = int(cur_outer * math.sin(rad))
            if -32 <= ox <= 31 and -32 <= oz <= 31:
                build.setblock(ox, y, oz, "stone_bricks")

            # Second outer wall (thickness)
            o2x = int((cur_outer - 1) * math.cos(rad))
            o2z = int((cur_outer - 1) * math.sin(rad))
            if -32 <= o2x <= 31 and -32 <= o2z <= 31:
                build.setblock(o2x, y, o2z, "stone_bricks")

            # Inner wall
            ix = int(cur_inner * math.cos(rad))
            iz = int(cur_inner * math.sin(rad))
            if -32 <= ix <= 31 and -32 <= iz <= 31:
                build.setblock(ix, y, iz, "stone_bricks")

        # Arched openings in outer wall — proper arches
        rel_y = y - base_y
        for arch_angle in range(0, 360, 8):
            rad = math.radians(arch_angle)
            ox = int(cur_outer * math.cos(rad))
            oz = int(cur_outer * math.sin(rad))
            if -32 <= ox <= 31 and -32 <= oz <= 31:
                # Arch shape: open in middle, closed at top with curve
                if rel_y >= 1 and rel_y <= 7:
                    arch_width = 3 - abs(rel_y - 4)
                    if arch_width > 0:
                        build.setblock(ox, y, oz, "air")
                        o2x = int((cur_outer-1) * math.cos(rad))
                        o2z = int((cur_outer-1) * math.sin(rad))
                        if -32 <= o2x <= 31 and -32 <= o2z <= 31:
                            build.setblock(o2x, y, o2z, "air")

    # Seating tiers - stepped
    for step in range(cur_inner, cur_outer - 2):
        tier_y = base_y + (step - cur_inner) // 2
        if tier_y < base_y + level_height:
            for angle in range(0, 360, 1):
                rad = math.radians(angle)
                sx = int(step * math.cos(rad))
                sz = int(step * math.sin(rad))
                if -32 <= sx <= 31 and -32 <= sz <= 31:
                    build.setblock(sx, tier_y, sz, "stone_brick_stairs")

# === HALF-COLUMNS (Engaged pillars) on exterior ===
for angle in range(0, 360, 10):
    rad = math.radians(angle)
    for level in range(levels):
        cur_outer = outer_r - level + 1
        base_y = 3 + level * level_height
        cx = int(cur_outer * math.cos(rad))
        cz = int(cur_outer * math.sin(rad))
        if -32 <= cx <= 31 and -32 <= cz <= 31:
            for y in range(base_y, base_y + level_height):
                # Different column orders per level
                if level == 0:
                    build.setblock(cx, y, cz, "quartz_block")  # Doric
                elif level == 1:
                    build.setblock(cx, y, cz, "quartz_block")  # Ionic
                elif level == 2:
                    build.setblock(cx, y, cz, "sandstone")     # Corinthian
                else:
                    build.setblock(cx, y, cz, "sandstone")     # Composite
            # Capital
            build.setblock(cx, base_y + level_height, cz, "quartz_block")
            # Base
            build.setblock(cx, base_y - 1, cz, "quartz_block")

# === GRAND ENTRANCES — 4 cardinal gates with proper arches ===
for gate_angle in [0, 90, 180, 270]:
    rad = math.radians(gate_angle)
    cos_a = math.cos(rad)
    sin_a = math.sin(rad)

    for r in range(inner_r - 1, outer_r + 2):
        gx = int(r * cos_a)
        gz = int(r * sin_a)
        if -32 <= gx <= 31 and -32 <= gz <= 31:
            # Main gate opening
            for y in range(3, 16):
                build.setblock(gx, y, gz, "air")
            # Width - clear adjacent
            for offset in [-1, 1, -2, 2]:
                orad = math.radians(gate_angle + offset)
                ox = int(r * math.cos(orad))
                oz = int(r * math.sin(orad))
                if -32 <= ox <= 31 and -32 <= oz <= 31:
                    for y in range(3, 14 - abs(offset)):
                        build.setblock(ox, y, oz, "air")

    # Gate frame - gold arch
    for r in range(inner_r, outer_r + 1):
        gx = int(r * cos_a)
        gz = int(r * sin_a)
        if -32 <= gx <= 31 and -32 <= gz <= 31:
            build.setblock(gx, 16, gz, "gold_block")

    # Flanking pillars
    for side in [-3, 3]:
        orad = math.radians(gate_angle + side)
        for r_pos in [inner_r, outer_r]:
            px = int(r_pos * math.cos(orad))
            pz = int(r_pos * math.sin(orad))
            if -32 <= px <= 31 and -32 <= pz <= 31:
                for y in range(3, 18):
                    build.setblock(px, y, pz, "quartz_block")
                build.setblock(px, 18, pz, "gold_block")

# === EMPEROR'S BOX — Elaborate VIP section (north) ===
box_z_start = -inner_r - 1
for x in range(-6, 7):
    for dz in range(0, 5):
        z = box_z_start + dz
        if -32 <= z <= 31:
            # Floor
            build.setblock(x, 8, z, "gold_block")
            # Ceiling
            build.setblock(x, 16, z, "gold_block")
            # Walls
            if abs(x) == 6:
                for y in range(9, 16):
                    build.setblock(x, y, z, "quartz_block")
            # Back wall
            if dz == 4:
                for y in range(9, 16):
                    build.setblock(x, y, z, "quartz_block")
            # Railing at front
            if dz == 0 and abs(x) <= 5:
                build.setblock(x, 9, z, "quartz_block")

# Throne
if -32 <= box_z_start + 3 <= 31:
    tz = box_z_start + 3
    build.fill(-1, 9, tz, 1, 9, tz, "gold_block")
    build.setblock(0, 10, tz, "diamond_block")
    build.setblock(0, 11, tz, "diamond_block")
    build.setblock(-1, 11, tz, "gold_block")
    build.setblock(1, 11, tz, "gold_block")
    build.setblock(0, 12, tz, "emerald_block")
    # Canopy
    build.fill(-2, 14, tz-1, 2, 14, tz+1, "purple_concrete")
    build.fill(-2, 15, tz-1, 2, 15, tz+1, "gold_block")

# === STATUES — Tall warrior figures at each gate ===
for gate_angle in [0, 90, 180, 270]:
    for side in [-5, 5]:
        rad = math.radians(gate_angle + side)
        sx = int((outer_r + 2) * math.cos(rad))
