    pz = random.randint(-28, 28)
    py = random.randint(5, 55)
    build.setblock(px, py, pz, "sea_lantern")

print("Floating Islands — Complete.")

# BURJ KHALIFA — Half scale, world's tallest building
# Real: 828m, Y-shaped cross-section with tiered setbacks
# Half scale: ~200 blocks + spire
build.clear()

# === GROUND PLAZA ===
build.fill(-31, 0, -31, 31, 0, 31, "light_gray_concrete")
# Decorative fountain pool
for x in range(-15, 16):
    for z in range(-15, -5):
        d = math.sqrt(x*x + (z+10)**2)
        if d < 10:
            build.setblock(x, 0, z, "light_blue_stained_glass")
            if d < 8 and d > 7:
                build.setblock(x, 1, z, "quartz_block")

# Y-shaped footprint: three wings at 120° apart
# Wing directions: South (0°), NE (120°), NW (240°)
wing_angles = [270, 30, 150]  # degrees

# Setback schedule (height -> wing_length, wing_width)
def get_wing_params(y):
    if y < 60:
        return 14, 5
    elif y < 90:
        return 11, 4
    elif y < 120:
        return 8, 3
    elif y < 150:
        return 6, 3
    elif y < 180:
        return 4, 2
    elif y < 200:
        return 3, 2
    else:
        return 2, 1

# === MAIN TOWER ===
max_h = 210

for y in range(1, max_h):
    wing_len, wing_w = get_wing_params(y)

    # Central core
    core_r = min(wing_w + 1, 6)
    for dx in range(-core_r, core_r + 1):
        for dz in range(-core_r, core_r + 1):
            if dx*dx + dz*dz <= core_r * core_r:
                if y % 12 == 0:
                    block = "white_concrete"
                elif (dx + dz + y) % 8 < 2:
                    block = "light_gray_stained_glass"
                else:
                    block = "light_gray_concrete"
                build.setblock(dx, y, dz, block)

    # Three wings
    for angle_deg in wing_angles:
        rad = math.radians(angle_deg)
        perp_rad = math.radians(angle_deg + 90)

        for d in range(core_r, core_r + wing_len):
            for w in range(-wing_w, wing_w + 1):
                wx = int(d * math.cos(rad) + w * math.cos(perp_rad))
                wz = int(d * math.sin(rad) + w * math.sin(perp_rad))

                if y % 12 == 0:
                    block = "white_concrete"
                elif d == core_r + wing_len - 1 or abs(w) == wing_w:
                    block = "light_gray_concrete"
                elif (d + w + y) % 6 < 2:
                    block = "light_gray_stained_glass"
                else:
                    block = "light_gray_concrete"
                build.setblock(wx, y, wz, block)

    # Hollow interior (for larger sections)
    if core_r > 3:
        ir = core_r - 2
        for dx in range(-ir, ir + 1):
            for dz in range(-ir, ir + 1):
                if dx*dx + dz*dz <= ir * ir:
                    build.setblock(dx, y, dz, "air")

    # Interior lighting every 10 floors
    if y % 10 == 0 and core_r > 2:
        build.setblock(0, y, 0, "sea_lantern")

# === SPIRE ===
spire_base = max_h
for y in range(spire_base, spire_base + 40):
    progress = (y - spire_base) / 40
    if progress < 0.5:
        build.setblock(0, y, 0, "iron_bars")
        build.setblock(1, y, 0, "iron_bars")
        build.setblock(0, y, 1, "iron_bars")
    elif progress < 0.8:
        build.setblock(0, y, 0, "iron_bars")
    else:
        build.setblock(0, y, 0, "iron_bars")

    # Cross supports
    if y % 8 == 0:
        for dx in range(-2, 3):
            build.setblock(dx, y, 0, "iron_bars")
            build.setblock(0, y, dx, "iron_bars")

build.setblock(0, spire_base + 40, 0, "redstone_lamp")
build.setblock(0, spire_base + 41, 0, "sea_lantern")

# === SETBACK TERRACE DETAILS ===
setback_ys = [60, 90, 120, 150, 180]
for sy in setback_ys:
    wing_len, wing_w = get_wing_params(sy - 1)
    next_len, next_w = get_wing_params(sy)

    for angle_deg in wing_angles:
        rad = math.radians(angle_deg)
        perp_rad = math.radians(angle_deg + 90)

        # Terrace floor on setback
        core_r_local = min(wing_w + 1, 6)
        for d in range(core_r_local + next_len, core_r_local + wing_len + 1):
            for w in range(-wing_w, wing_w + 1):
                wx = int(d * math.cos(rad) + w * math.cos(perp_rad))
                wz = int(d * math.sin(rad) + w * math.sin(perp_rad))
                build.setblock(wx, sy, wz, "white_concrete")
                # Railing lights
                if abs(w) == wing_w or d == core_r_local + wing_len:
                    build.setblock(wx, sy + 1, wz, "sea_lantern")

# === NIGHT LIGHTING — lit windows scattered ===
for y in range(3, max_h, 3):
    wing_len, wing_w = get_wing_params(y)
    core_r_local = min(wing_w + 1, 6)

    for angle_deg in wing_angles:
        rad = math.radians(angle_deg)
        perp_rad = math.radians(angle_deg + 90)

        if random.random() > 0.5:
            d = core_r_local + random.randint(0, max(0, wing_len - 1))
            w = random.randint(-wing_w + 1, wing_w - 1)
            wx = int(d * math.cos(rad) + w * math.cos(perp_rad))
            wz = int(d * math.sin(rad) + w * math.sin(perp_rad))
            build.setblock(wx, y, wz, "sea_lantern")

# === GROUND FEATURES ===
# Trees around plaza
tree_positions = [(-25, -25), (25, -25), (-25, 25), (25, 25), (-20, 0), (20, 0)]
for tx, tz in tree_positions:
    build.setblock(tx, 0, tz, "grass_block")
    for ty in range(1, 5):
        build.setblock(tx, ty, tz, "oak_log")
    for dx in range(-2, 3):
        for dz in range(-2, 3):
            for dy in range(4, 7):
                if abs(dx) + abs(dz) <= 2:
                    build.setblock(tx + dx, dy, tz + dz, "oak_leaves")

# Street lamps
for lx in range(-28, 29, 7):
    for lz in [-28, 28]:
        for ly in range(1, 5):
            build.setblock(lx, ly, lz, "iron_bars")
        build.setblock(lx, 5, lz, "lantern")

print("Burj Khalifa (Half Scale) — Complete.")
